import React, { useState } from "react";
import Images from "../Images";

const ToggleImage = props => {
  const [hider, updateHider] = useState(true);
  const hideImage = e => {
    updateHider(!hider);
  };
  return (
    <div>
      {
        hider ? <button onClick={hideImage}>Show Image</button> : <button onClick={hideImage}>Hide Image</button>
      }
      {
        hider &&  <Images baseUrlImage={props.baseUrlImage} baseUrl={props.baseUrl} />
      }
    </div>
  );
};

export default ToggleImage;
